def create_project(project_name, path="."):
    if project_name not in PROJECTS_TEMPLATES:
        print("المشاريع المتاحة: {}".format(", ".join(PROJECTS_TEMPLATES.keys())))
        return False
    
    project_path = os.path.join(path, project_name)
    
    try:
        os.makedirs(project_path, exist_ok=True)
        
        template = PROJECTS_TEMPLATES[project_name]
        
        for file_name, content in template.items():
            file_path = os.path.join(project_path, file_name)
            with open(file_path, 'w', encoding='utf-8') as f:
                f.write(content)
        
        print("تم إنشاء المشروع بنجاح في: {}".format(project_path))
        print("\nلتشغيل المشروع:")
        print("  cd {}".format(project_name))
        print("  pyarabify main.py")
        
        return True
        
    except Exception as e:
        print("خطأ في إنشاء المشروع: {}".format(e))
        return False

def list_projects():
    print("المشاريع المتاحة:")
    print("=" * 50)
    for name in PROJECTS_TEMPLATES.keys():
        print("- {}".format(name))
    print("=" * 50)

انشئ_مشروع = create_project
