# Popular languages for quick access (top submenu)
POPULAR_LANGUAGES = [
    "auto",
    "en",
    "es",
    "zh",
    "fr",
    "de",
    "ja",
    "pt",
    "ru",
    "ar",
    "hi",
    "ko",
]


def get_language_name(code: str) -> str:
    """
    Get language name from code.

    Args:
        code: Language code (e.g., "en", "es")

    Returns:
        Language name or code if not found
    """
    return WHISPER_LANGUAGES.get(code, code)


def get_all_languages() -> dict[str, str]:
    """Get all supported languages."""
    return WHISPER_LANGUAGES.copy()


def get_popular_languages() -> list[str]:
    """Get list of popular language codes."""
    return POPULAR_LANGUAGES.copy()


def is_valid_language(code: str) -> bool:
    """
    Check if language code is valid.

    Args:
        code: Language code to check

    Returns:
        True if valid, False otherwise
    """
    return code in WHISPER_LANGUAGES
